import React from 'react';
import GoogleMapReact from 'google-map-react';

import useStyles from './style';

const Map = ({ setCoordinates, setBounds, coordinates }) => {
    const classes = useStyles();
    const isMobile = useMediaQuery('(max-width:600px)');

    const handleChildClick = (key, childProps) => {
        // Handle the click event here, you can access the key and childProps
        console.log(`Clicked on child with key: ${key}`);
        // You can also perform other actions or set state based on the click event
    };

    return (
        <div className={classes.mapContainer}>
            <GoogleMapReact
                bootstrapURLKeys={{ key: 'AIzaSyB9fhG4GZtgJq4Lx4cRKpXmJvbFOtbH8dU' }}
                defaultCenter={coordinates}
                center={coordinates}
                defaultZoom={14}
                margin={[50, 50, 50, 50]}
                options={''}
                onChildClick={handleChildClick} // Add the click handler here
                onChange={(e) => {
                    setCoordinates({ lat: e.center.lat, lng: e.center.lng });
                    setBounds({ ne: e.marginBounds.ne, sw: e.marginBounds.sw });
                }}
            >
                {/* Add markers or other map components here */}
            </GoogleMapReact>
        </div>
    );
}

export default Map;
